package Easy;

public class Evenodd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number = 10;

		// Find out if the number above is even or odd
		if (number % 2 == 0) {
		  System.out.println(number + " is even.");
		} else {
		  System.out.println(number + " is odd.");
		}
	}

}
